package ARRAY1D;

public class ExceptionHandling {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		try {
			while (true) {
				int num = 10;
				Integer sum = 10;
				System.out.println(num + sum + "Yash");

			}
		} catch (Exception e) {

		} finally {
			System.out.println("welcome");
		}
	}

}
